import React, { useEffect, useState } from 'react';
import ReactDOM from 'react-dom';
import { configureStore, createSlice } from '@reduxjs/toolkit';
import thunkMiddleware from 'redux-thunk';
import { Provider, useSelector, useDispatch } from 'react-redux';
import axios from 'axios';
import { List, Spin } from 'antd';

const initialState = {
  pokemons: [],
  loading: false,
  error: null,
};

const pokemonSlice = createSlice({
  name: 'pokemon',
  initialState,
  reducers: {
    fetchPokemonsStart(state) {
      state.loading = true;
      state.error = null;
    },
    fetchPokemonsSuccess(state, action) {
      state.loading = false;
      state.pokemons = action.payload;
    },
    fetchPokemonsFailure(state, action) {
      state.loading = false;
      state.error = action.payload;
    },
  },
});

const { fetchPokemonsStart, fetchPokemonsSuccess, fetchPokemonsFailure } = pokemonSlice.actions;

const fetchPokemons = () => {
  return async (dispatch) => {
    dispatch(fetchPokemonsStart());

    try {
      const response = await axios.get('https://pokeapi.co/api/v2/pokemon?limit=151');
      const pokemons = response.data.results;
      dispatch(fetchPokemonsSuccess(pokemons));
    } catch (error) {
      dispatch(fetchPokemonsFailure(error.message));
    }
  };
};

const store = configureStore({
  reducer: { pokemon: pokemonSlice.reducer },
  middleware: [thunkMiddleware],
});

const App = () => {
  const pokemons = useSelector((state) => state.pokemon.pokemons);
  const dispatch = useDispatch();
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    dispatch(fetchPokemons());
  }, [dispatch]);

  const handleImageLoad = () => {
    setIsLoading(false);
  };

  const handleImageError = (event) => {
    event.target.onerror = null; // Prevent infinite loop if the error message image also fails to load
    event.target.src = 'https://via.placeholder.com/96x96?text=Error'; // Show error message image
    setIsLoading(false);
  };

  return (
    <div>
      <h1>Pokemon List</h1>
      <List
        grid={{ gutter: 16, column: 4 }}
        dataSource={pokemons}
        renderItem={(pokemon) => (
          <List.Item>
            {isLoading ? (
              <Spin size="large" />
            ) : (
              <img
                src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/official-artwork/${pokemon.id}.png`}
                alt={pokemon.name}
                onLoad={handleImageLoad}
                onError={handleImageError}
              />
            )}
            <span>{pokemon.name}</span>
          </List.Item>
        )}
      />
    </div>
  );
};

ReactDOM.render(
  <Provider store={store}>
    <App />
  </Provider>,
  document.getElementById('root')
);
