import React, { useEffect, useState } from 'react';
import ReactDOM from 'react-dom';
import { configureStore, createSlice } from '@reduxjs/toolkit';
import thunkMiddleware from 'redux-thunk';
import { Provider, useSelector, useDispatch } from 'react-redux';
import axios from 'axios';
import { List } from 'antd';

const initialState = {
  pokemons: [],
  loading: false,
  error: null,
};

const pokemonSlice = createSlice({
  name: 'pokemon',
  initialState,
  reducers: {
    fetchPokemonsStart(state) {
      state.loading = true;
      state.error = null;
    },
    fetchPokemonsSuccess(state, action) {
      state.loading = false;
      state.pokemons = action.payload;
    },
    fetchPokemonsFailure(state, action) {
      state.loading = false;
      state.error = action.payload;
    },
  },
});

const { fetchPokemonsStart, fetchPokemonsSuccess, fetchPokemonsFailure } = pokemonSlice.actions;

const fetchPokemon = () => {
  return async (dispatch) => {
    dispatch(fetchPokemonsStart());

    try {
      const response = await axios.get('https://pokeapi.co/api/v2/pokemon?limit=151');
      const pokemons = response.data.results;
      dispatch(fetchPokemonsSuccess(pokemons));
    } catch (error) {
      dispatch(fetchPokemonsFailure(error.message));
    }
  };
};

const store = configureStore({
  reducer: { pokemon: pokemonSlice.reducer },
  middleware: [thunkMiddleware],
});

const App = () => {
  const [search, setSearch] = useState('');
  const pokemonList = useSelector((state) => state.pokemon.list);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchPokemon());
  }, [dispatch]);

  const filteredPokemon = pokemonList.filter((pokemon) =>
    pokemon.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div>
      <input type="text" onChange={(e) => setSearch(e.target.value)} />
      <List
        grid={{ gutter: 16, column: 4 }}
        dataSource={filteredPokemon}
        renderItem={(pokemon) => (
          <List.Item>
            <img src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/official-artwork/${pokemon.name}.png`} alt={pokemon.name} />
            <span>{pokemon.name}</span>
            <p>{pokemon.name}</p>
          </List.Item>
        )}
      />
    </div>
  );
}

ReactDOM.render(
  <Provider store={store}>
    <App />
  </Provider>,
  document.getElementById('root')
);
