import React, { useState } from 'react';
import axios from 'axios';
import { List } from 'antd';
import { createSlice, configureStore, createAsyncThunk } from '@reduxjs/toolkit';

const pokemonSlice = createSlice({
  name: 'pokemon',
  initialState: { data: [] },
  reducers: {
    setPokemon: (state, action) => {
      state.data = action.payload;
    },
  },
});

const fetchPokemon = createAsyncThunk('pokemon/fetchPokemon', async () => {
  const response = await axios.get('https://pokeapi.co/api/v2/pokemon?limit=151');
  return response.data.results;
});

const store = configureStore({ reducer: pokemonSlice.reducer });

store.dispatch(fetchPokemon());

const App = () => {
  const [pokemonList, setPokemonList] = useState([]);

  store.subscribe(() => {
    const pokemonData = store.getState().pokemon.data;
    setPokemonList(pokemonData);
  });

  const renderPokemon = (pokemon) => {
    return (
      <List.Item>
        <img src={pokemon.sprites.front_default} alt={pokemon.name} />
        <List.Item.Meta title={pokemon.name} />
      </List.Item>
    );
  };

  return (
    <div>
      <h1>Pokemon List</h1>
      <List
        grid={{ gutter: 16, column: 4 }}
        dataSource={pokemonList}
        renderItem={(pokemon) => renderPokemon(pokemon)}
      />
    </div>
  );
};

export default App;
