import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { List, Avatar } from "antd";
import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";

const pokemonSlice = createSlice({
  name: "pokemon",
  initialState: {
    pokemons: [],
    status: "idle",
    error: null,
  },
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(fetchPokemons.pending, (state) => {
        state.status = "loading";
      })
      .addCase(fetchPokemons.fulfilled, (state, action) => {
        state.status = "succeeded";
        state.pokemons = action.payload;
      })
      .addCase(fetchPokemons.rejected, (state, action) => {
        state.status = "failed";
        state.error = action.error.message;
      });
  },
});

const fetchPokemons = createAsyncThunk("pokemon/fetchPokemons", async () => {
  const response = await axios.get("https://pokeapi.co/api/v2/pokemon?limit=151");
  return response.data.results;
});

function App() {
  const dispatch = useDispatch();
  const { pokemons, status, error } = useSelector((state) => state.pokemon);

  useEffect(() => {
    dispatch(fetchPokemons());
  }, [dispatch]);

  if (status === "loading") {
    return <div>Loading...</div>;
  } else if (status === "failed") {
    return <div>{error}</div>;
  }

  return (
    <List
      itemLayout="horizontal"
      dataSource={pokemons}
      renderItem={(pokemon) => (
        <List.Item>
          <List.Item.Meta
            avatar={<Avatar src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${pokemon.url.split("/")[6]}.png`} />}
            title={<a href={`https://www.pokemon.com/us/pokedex/${pokemon.name}`} target="_blank" rel="noopener noreferrer">{pokemon.name}</a>}
          />
        </List.Item>
      )}
    />
  );
}

export default App;
