import React, { useState, useEffect } from 'react';
import { List } from 'antd';
import { createSlice, configureStore, createAsyncThunk } from '@reduxjs/toolkit';
import { useSelector, useDispatch } from 'react-redux';
import axios from 'axios';

const pokemonSlice = createSlice({
  name: 'pokemon',
  initialState: {
    list: [],
    status: 'idle',
    error: null,
  },
  reducers: {
    addAll: (state, action) => {
      state.list = action.payload;
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(fetchPokemon.pending, (state) => {
        state.status = 'loading';
      })
      .addCase(fetchPokemon.fulfilled, (state, action) => {
        state.status = 'succeeded';
        state.list = action.payload;
      })
      .addCase(fetchPokemon.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.error.message;
      });
  },
});

const fetchPokemon = createAsyncThunk('pokemon/fetchPokemon', async () => {
  const response = await axios.get('https://pokeapi.co/api/v2/pokemon?limit=151');
  const { results } = response.data;
  const data = await Promise.all(
    results.map(async (pokemon) => {
      const spriteResponse = await axios.get(pokemon.url);
      const { id, name, sprites } = spriteResponse.data;
      return { id, name, spriteUrl: sprites.front_default };
    })
  );
  return data;
});

const store = configureStore({
  reducer: {
    pokemon: pokemonSlice.reducer,
  },
});

function App() {
  const [search, setSearch] = useState('');
  const pokemonList = useSelector((state) => state.pokemon.list);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchPokemon());
  }, [dispatch]);

  const filteredPokemon = pokemonList.filter((pokemon) =>
    pokemon.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div>
      <input type="text" onChange={(e) => setSearch(e.target.value)} />
      <List
        grid={{ gutter: 16, column: 4 }}
        dataSource={filteredPokemon}
        renderItem={(pokemon) => (
          <List.Item>
            <img alt={pokemon.name} src={pokemon.spriteUrl} style={{ height: '100px' }} />
            <p>{pokemon.name}</p>
          </List.Item>
        )}
      />
    </div>
  );
}

export default App;
